INSERT INTO reviews (review_id, user_id, restaurant_id, order_id, star_rating, review_title, review_text, is_verified_visit, helpful_count, is_edited, edited_at, created_at, updated_at) VALUES
('review_001', 'user_001', 'rest_001', 'order_001', 5, 'Best Italian in Portland!', 'Absolutely loved the pasta carbonara. The handmade pasta was perfectly cooked and the sauce was rich and creamy. The pizza was also fantastic with a crispy crust. Will definitely be back!', true, 12, false, NULL, '2024-01-06T10:00:00Z', '2024-01-06T10:00:00Z'),
('review_002', 'user_002', 'rest_003', 'order_002', 5, 'Incredible sushi experience', 'The omakase was outstanding. Every piece of sushi was perfectly prepared with the freshest fish. The chef clearly knows what he''s doing. Best sushi I''ve had in years!', true, 18, false, NULL, '2024-01-07T11:00:00Z', '2024-01-07T11:00:00Z'),
('review_003', 'user_003', 'rest_005', 'order_003', 4, 'Great burgers!', 'The classic cheeseburger was delicious and cooked to perfection. Truffle fries were amazing. Only complaint is the wait time was a bit long for pickup.', true, 8, false, NULL, '2024-01-07T15:00:00Z', '2024-01-07T15:00:00Z'),
('review_004', 'user_004', 'rest_002', 'order_004', 5, 'Authentic Indian cuisine', 'The butter chicken was the best I''ve ever had. Perfectly spiced and the meat was so tender. The palak paneer was also excellent. Highly recommend!', true, 15, false, NULL, '2024-01-08T10:00:00Z', '2024-01-08T10:00:00Z'),
('review_005', 'user_005', 'rest_007', 'order_005', 5, 'Taco Tuesday perfection', 'The carne asada tacos were amazing! Fresh ingredients and the meat was perfectly seasoned. Great value with the Tuesday discount too.', true, 10, false, NULL, '2024-01-10T09:00:00Z', '2024-01-10T09:00:00Z'),
('review_006', 'user_006', 'rest_004', 'order_006', 4, 'Healthy and delicious', 'Love this place for a healthy meal. The acai bowl and Buddha bowl were both fresh and tasty. Great spot for vegetarians!', true, 7, false, NULL, '2024-01-08T16:00:00Z', '2024-01-08T16:00:00Z'),
('review_007', 'user_007', 'rest_010', 'order_007', 5, 'French bistro excellence', 'An absolutely wonderful dining experience. The coq au vin was traditional and delicious, and the steak frites were cooked perfectly. The crme brle was the perfect ending. Worth every penny!', true, 22, false, NULL, '2024-01-11T10:00:00Z', '2024-01-11T10:00:00Z'),
('review_008', 'user_008', 'rest_006', 'order_008', 4, 'Solid Chinese food', 'The lo mein was tasty and the portions were generous. Dumplings were perfectly crispy. Good value for money.', true, 5, false, NULL, '2024-01-09T09:00:00Z', '2024-01-09T09:00:00Z'),
('review_009', 'user_009', 'rest_011', 'order_009', 5, 'Quick and tasty Thai', 'Perfect for a quick lunch. The pad Thai had great flavor and the green curry was perfectly spiced. Fast service too!', true, 9, false, NULL, '2024-01-09T14:00:00Z', '2024-01-09T14:00:00Z'),
('review_010', 'user_010', 'rest_016', 'order_010', 5, 'Ramen heaven', 'The tonkotsu ramen was incredible. Rich, flavorful broth and perfectly cooked noodles. This is my new go-to ramen spot!', true, 16, false, NULL, '2024-01-10T20:00:00Z', '2024-01-10T20:00:00Z'),
('review_011', 'user_011', 'rest_019', 'order_011', 4, 'Great breakfast spot', 'Pancakes were fluffy and the eggs benedict was delicious. Coffee was good too. Can get busy on weekends but worth the wait.', true, 11, false, NULL, '2024-01-11T11:00:00Z', '2024-01-11T11:00:00Z'),
('review_012', 'user_012', 'rest_012', 'order_012', 5, 'Premium steakhouse', 'The ribeye was cooked to perfection - perfectly charred outside and juicy inside. The truffle mac and cheese was decadent. Expensive but worth it for a special occasion.', true, 14, false, NULL, '2024-01-10T09:00:00Z', '2024-01-10T09:00:00Z'),
('review_013', 'user_013', 'rest_014', 'order_013', 5, 'Authentic Ethiopian food', 'Such a unique and delicious experience! The vegetarian platter was incredible with so many different flavors. Loved eating with injera bread.', true, 13, false, NULL, '2024-01-09T10:00:00Z', '2024-01-09T10:00:00Z'),
('review_014', 'user_014', 'rest_015', 'order_014', 5, 'BBQ perfection', 'The brisket was so tender it melted in my mouth. The ribs were fall-off-the-bone good. Best BBQ in Portland hands down!', true, 19, false, NULL, '2024-01-10T20:00:00Z', '2024-01-10T20:00:00Z'),
('review_015', 'user_015', 'rest_018', 'order_015', 4, 'Good pizza', 'Both pizzas were tasty with good quality ingredients. The crust could have been a bit crispier but overall very good.', true, 6, false, NULL, '2024-01-11T21:00:00Z', '2024-01-11T21:00:00Z'),
('review_016', 'user_001', 'rest_020', NULL, 5, 'Fresh seafood', 'The lobster roll was incredible - so much meat! Oysters were fresh and delicious. Great spot for seafood lovers.', false, 8, false, NULL, '2024-01-08T14:00:00Z', '2024-01-08T14:00:00Z'),
('review_017', 'user_002', 'rest_015', 'order_032', 4, 'Great BBQ', 'Pulled pork was fantastic and the mac and cheese was creamy and delicious. Sauce selection was great.', true, 7, false, NULL, '2024-01-06T10:00:00Z', '2024-01-06T10:00:00Z'),
('review_018', 'user_003', 'rest_013', 'order_033', 5, 'Love poke bowls here', 'Always fresh and the portions are generous. The spicy salmon is my favorite. Great healthy option!', true, 10, false, NULL, '2024-01-04T09:00:00Z', '2024-01-04T09:00:00Z'),
('review_019', 'user_004', 'rest_011', 'order_034', 4, 'Reliable Thai food', 'Pad Thai and curry were both good. Appreciate the spice level options. Quick delivery too.', true, 5, false, NULL, '2024-01-03T10:00:00Z', '2024-01-03T10:00:00Z'),
('review_020', 'user_005', 'rest_001', 'order_035', 5, 'Never disappoints', 'Third time ordering and it''s always excellent. The pasta is consistently perfect. My go-to Italian spot.', true, 9, false, NULL, '2024-01-02T11:00:00Z', '2024-01-02T11:00:00Z'),
('review_021', 'user_006', 'rest_020', 'order_036', 5, 'Best fish and chips', 'The batter was crispy and light, fish was flaky and fresh. Generous portions. Highly recommend!', true, 12, false, NULL, '2023-12-31T10:00:00Z', '2023-12-31T10:00:00Z'),
('review_022', 'user_007', 'rest_009', 'order_037', 5, 'Korean BBQ at its finest', 'The galbi was marinated perfectly. Great atmosphere and the side dishes (banchan) were all delicious.', true, 15, false, NULL, '2023-12-29T10:00:00Z', '2023-12-29T10:00:00Z'),
('review_023', 'user_008', 'rest_019', 'order_038', 4, 'Good breakfast', 'Pancakes were good and the portions were large. Coffee could be stronger but overall a nice breakfast.', true, 4, false, NULL, '2023-12-27T12:00:00Z', '2023-12-27T12:00:00Z'),
('review_024', 'user_009', 'rest_003', 'order_039', 5, 'Amazing sushi', 'Every roll was beautifully presented and tasted incredible. You can tell they use high quality fish.', true, 17, false, NULL, '2023-12-27T09:00:00Z', '2023-12-27T09:00:00Z'),
('review_025', 'user_010', 'rest_018', 'order_040', 4, 'Solid pizza joint', 'Good pizza at a reasonable price. Not the best I''ve ever had but definitely satisfying.', true, 6, false, NULL, '2023-12-26T10:00:00Z', '2023-12-26T10:00:00Z'),
('review_026', 'user_011', 'rest_016', 'order_041', 5, 'Ramen is life', 'The broth is so rich and flavorful. Noodles have perfect texture. This is my comfort food spot.', true, 13, false, NULL, '2023-12-25T10:00:00Z', '2023-12-25T10:00:00Z'),
('review_027', 'user_012', 'rest_014', 'order_042', 4, 'Interesting flavors', 'First time trying Ethiopian food and I loved it! The meat platter had so many unique flavors. Will be back to try more.', true, 8, false, NULL, '2023-12-24T10:00:00Z', '2023-12-24T10:00:00Z'),
('review_028', 'user_013', 'rest_008', 'order_043', 5, 'Mediterranean delight', 'The mezze platter was perfect for sharing. Everything was fresh and flavorful. The lamb kebabs were perfectly seasoned.', true, 11, false, NULL, '2023-12-23T10:00:00Z', '2023-12-23T10:00:00Z'),
('review_029', 'user_014', 'rest_005', 'order_044', 4, 'Tasty burgers', 'Both burgers were juicy and flavorful. Good selection of toppings. Fries were crispy.', true, 7, false, NULL, '2023-12-22T10:00:00Z', '2023-12-22T10:00:00Z'),
('review_030', 'user_015', 'rest_017', 'order_045', 5, 'Farm to table excellence', 'You can taste how fresh everything is. The seasonal menu is creative and delicious. Loved both the salmon and ribeye.', true, 20, false, NULL, '2023-12-21T10:00:00Z', '2023-12-21T10:00:00Z'),
('review_031', 'user_001', 'rest_012', 'order_046', 5, 'Special occasion perfection', 'Celebrated an anniversary here and it was perfect. The steak and lobster were both cooked to perfection. Service was excellent.', true, 16, false, NULL, '2024-01-12T10:00:00Z', '2024-01-12T10:00:00Z'),
('review_032', 'user_002', 'rest_002', NULL, 4, 'Solid Indian food', 'Good curries with authentic flavors. Naan bread was fresh and fluffy. Good portion sizes.', false, 5, false, NULL, '2024-01-05T14:00:00Z', '2024-01-05T14:00:00Z'),
('review_033', 'user_003', 'rest_009', NULL, 5, 'Love this place', 'The bibimbap is always perfect. Great for a quick but satisfying dinner.', false, 8, false, NULL, '2024-01-03T16:00:00Z', '2024-01-03T16:00:00Z'),
('review_034', 'user_005', 'rest_010', 'order_048', 5, 'Romantic and delicious', 'Perfect date night spot. The duck confit was amazing. Great wine selection too.', true, 14, false, NULL, '2023-12-18T11:00:00Z', '2023-12-18T11:00:00Z'),
('review_035', 'user_008', 'rest_006', NULL, 3, 'Average', 'Food was okay but nothing special. Service was a bit slow. Might give it another try.', false, 2, false, NULL, '2024-01-02T13:00:00Z', '2024-01-02T13:00:00Z'),
('review_036', 'user_009', 'rest_016', NULL, 5, 'Can''t get enough', 'Been here five times this month. The ramen is addictive! Try the spicy miso.', false, 11, false, NULL, '2024-01-05T17:00:00Z', '2024-01-05T17:00:00Z'),
('review_037', 'user_010', 'rest_007', NULL, 4, 'Great for groups', 'Took a group of friends here and everyone loved their meals. The tableside guacamole is a must!', false, 6, true, '2024-01-04T15:00:00Z', '2024-01-03T12:00:00Z', '2024-01-04T15:00:00Z'),
('review_038', 'user_012', 'rest_018', 'order_027', 4, 'Good pizza delivery', 'Pizza arrived hot and fresh. The truffle mushroom pizza was excellent. Good value.', true, 5, false, NULL, '2024-01-09T10:00:00Z', '2024-01-09T10:00:00Z'),
('review_039', 'user_013', 'rest_003', 'order_028', 5, 'Sushi perfection', 'The fish quality is unmatched. Presentation is beautiful. A bit pricey but worth every penny.', true, 19, false, NULL, '2024-01-08T11:00:00Z', '2024-01-08T11:00:00Z'),
('review_040', 'user_015', 'rest_002', 'order_030', 4, 'Tasty Indian', 'The tandoori chicken was delicious and the paneer tikka was great too. Delivery was prompt.', true, 7, false, NULL, '2024-01-07T10:00:00Z', '2024-01-07T10:00:00Z');
