-- ============================================
-- TABLE CREATION
-- ============================================

-- users table
CREATE TABLE users (
    user_id TEXT PRIMARY KEY,
    email TEXT UNIQUE NOT NULL,
    password_hash TEXT NOT NULL,
    full_name TEXT NOT NULL,
    phone_number TEXT,
    profile_picture_url TEXT,
    is_verified BOOLEAN NOT NULL DEFAULT false,
    member_since TEXT NOT NULL,
    notification_preferences JSONB NOT NULL DEFAULT '{"order_updates_email":true,"order_updates_push":true,"promotions_email":true,"promotions_push":true,"weekly_picks_email":true,"weekly_picks_push":true,"new_restaurants_email":false,"new_restaurants_push":false,"review_responses_email":true,"review_responses_push":true}',
    location_permission_granted BOOLEAN NOT NULL DEFAULT false,
    profile_public BOOLEAN NOT NULL DEFAULT true,
    reviews_public BOOLEAN NOT NULL DEFAULT true,
    last_login TEXT,
    created_at TEXT NOT NULL,
    updated_at TEXT NOT NULL
);

-- restaurants table
CREATE TABLE restaurants (
    restaurant_id TEXT PRIMARY KEY,
    restaurant_name TEXT NOT NULL,
    description TEXT,
    cuisine_types JSONB NOT NULL DEFAULT '[]',
    price_range INTEGER NOT NULL,
    street_address TEXT NOT NULL,
    apartment_suite TEXT,
    city TEXT NOT NULL,
    state TEXT NOT NULL,
    zip_code TEXT NOT NULL,
    latitude NUMERIC NOT NULL,
    longitude NUMERIC NOT NULL,
    phone_number TEXT NOT NULL,
    primary_hero_image_url TEXT,
    average_rating NUMERIC NOT NULL DEFAULT 0,
    total_review_count INTEGER NOT NULL DEFAULT 0,
    total_order_count INTEGER NOT NULL DEFAULT 0,
    is_currently_open BOOLEAN NOT NULL DEFAULT false,
    accepts_delivery BOOLEAN NOT NULL DEFAULT true,
    accepts_pickup BOOLEAN NOT NULL DEFAULT true,
    delivery_fee NUMERIC NOT NULL DEFAULT 0,
    minimum_order_amount NUMERIC NOT NULL DEFAULT 0,
    delivery_radius_miles NUMERIC NOT NULL DEFAULT 5,
    estimated_prep_time_minutes INTEGER NOT NULL DEFAULT 30,
    estimated_delivery_time_minutes INTEGER NOT NULL DEFAULT 45,
    is_featured BOOLEAN NOT NULL DEFAULT false,
    featured_week_start TEXT,
    featured_description TEXT,
    is_active BOOLEAN NOT NULL DEFAULT true,
    created_at TEXT NOT NULL,
    updated_at TEXT NOT NULL
);

-- restaurant_hours table
CREATE TABLE restaurant_hours (
    hours_id TEXT PRIMARY KEY,
    restaurant_id TEXT NOT NULL REFERENCES restaurants(restaurant_id),
    day_of_week INTEGER NOT NULL,
    open_time TEXT,
    close_time TEXT,
    is_closed BOOLEAN NOT NULL DEFAULT false,
    created_at TEXT NOT NULL,
    updated_at TEXT NOT NULL
);

-- restaurant_photos table
CREATE TABLE restaurant_photos (
    photo_id TEXT PRIMARY KEY,
    restaurant_id TEXT NOT NULL REFERENCES restaurants(restaurant_id),
    photo_url TEXT NOT NULL,
    caption TEXT,
    display_order INTEGER NOT NULL DEFAULT 0,
    uploaded_by_user_id TEXT REFERENCES users(user_id),
    created_at TEXT NOT NULL
);

-- menu_categories table
CREATE TABLE menu_categories (
    category_id TEXT PRIMARY KEY,
    restaurant_id TEXT NOT NULL REFERENCES restaurants(restaurant_id),
    category_name TEXT NOT NULL,
    display_order INTEGER NOT NULL DEFAULT 0,
    created_at TEXT NOT NULL,
    updated_at TEXT NOT NULL
);

-- menu_items table
CREATE TABLE menu_items (
    menu_item_id TEXT PRIMARY KEY,
    restaurant_id TEXT NOT NULL REFERENCES restaurants(restaurant_id),
    category_id TEXT NOT NULL REFERENCES menu_categories(category_id),
    item_name TEXT NOT NULL,
    description TEXT,
    base_price NUMERIC NOT NULL,
    item_photo_url TEXT,
    dietary_preferences JSONB NOT NULL DEFAULT '[]',
    allergen_info JSONB NOT NULL DEFAULT '[]',
    spice_level INTEGER,
    is_popular BOOLEAN NOT NULL DEFAULT false,
    is_available BOOLEAN NOT NULL DEFAULT true,
    display_order INTEGER NOT NULL DEFAULT 0,
    created_at TEXT NOT NULL,
    updated_at TEXT NOT NULL
);

-- menu_item_sizes table
CREATE TABLE menu_item_sizes (
    size_id TEXT PRIMARY KEY,
    menu_item_id TEXT NOT NULL REFERENCES menu_items(menu_item_id),
    size_name TEXT NOT NULL,
    price_adjustment NUMERIC NOT NULL DEFAULT 0,
    display_order INTEGER NOT NULL DEFAULT 0,
    created_at TEXT NOT NULL
);

-- menu_item_addons table
CREATE TABLE menu_item_addons (
    addon_id TEXT PRIMARY KEY,
    menu_item_id TEXT NOT NULL REFERENCES menu_items(menu_item_id),
    addon_name TEXT NOT NULL,
    addon_price NUMERIC NOT NULL,
    display_order INTEGER NOT NULL DEFAULT 0,
    created_at TEXT NOT NULL
);

-- menu_item_modifications table
CREATE TABLE menu_item_modifications (
    modification_id TEXT PRIMARY KEY,
    menu_item_id TEXT NOT NULL REFERENCES menu_items(menu_item_id),
    modification_name TEXT NOT NULL,
    price_adjustment NUMERIC NOT NULL DEFAULT 0,
    display_order INTEGER NOT NULL DEFAULT 0,
    created_at TEXT NOT NULL
);

-- discounts table
CREATE TABLE discounts (
    discount_id TEXT PRIMARY KEY,
    restaurant_id TEXT NOT NULL REFERENCES restaurants(restaurant_id),
    discount_type TEXT NOT NULL,
    discount_value NUMERIC NOT NULL,
    coupon_code TEXT UNIQUE,
    qr_code_data TEXT,
    description TEXT NOT NULL,
    terms_conditions TEXT,
    minimum_order_amount NUMERIC,
    excluded_items JSONB NOT NULL DEFAULT '[]',
    valid_days JSONB NOT NULL DEFAULT '[0,1,2,3,4,5,6]',
    is_one_time_use BOOLEAN NOT NULL DEFAULT true,
    max_redemptions_per_user INTEGER,
    total_redemption_limit INTEGER,
    current_redemption_count INTEGER NOT NULL DEFAULT 0,
    is_active BOOLEAN NOT NULL DEFAULT true,
    start_date TEXT NOT NULL,
    end_date TEXT NOT NULL,
    is_local_picks_exclusive BOOLEAN NOT NULL DEFAULT false,
    created_at TEXT NOT NULL,
    updated_at TEXT NOT NULL
);

-- saved_payment_methods table
CREATE TABLE saved_payment_methods (
    payment_method_id TEXT PRIMARY KEY,
    user_id TEXT NOT NULL REFERENCES users(user_id),
    payment_label TEXT NOT NULL,
    card_type TEXT NOT NULL,
    last_four_digits TEXT NOT NULL,
    expiration_month TEXT NOT NULL,
    expiration_year TEXT NOT NULL,
    billing_zip_code TEXT NOT NULL,
    is_default BOOLEAN NOT NULL DEFAULT false,
    created_at TEXT NOT NULL,
    updated_at TEXT NOT NULL
);

-- orders table
CREATE TABLE orders (
    order_id TEXT PRIMARY KEY,
    user_id TEXT NOT NULL REFERENCES users(user_id),
    restaurant_id TEXT NOT NULL REFERENCES restaurants(restaurant_id),
    order_type TEXT NOT NULL,
    order_status TEXT NOT NULL,
    delivery_street_address TEXT,
    delivery_apartment_suite TEXT,
    delivery_city TEXT,
    delivery_state TEXT,
    delivery_zip_code TEXT,
    special_instructions TEXT,
    subtotal NUMERIC NOT NULL,
    discount_amount NUMERIC NOT NULL DEFAULT 0,
    discount_id TEXT REFERENCES discounts(discount_id),
    delivery_fee NUMERIC NOT NULL DEFAULT 0,
    tax NUMERIC NOT NULL,
    tip NUMERIC NOT NULL DEFAULT 0,
    grand_total NUMERIC NOT NULL,
    payment_method_id TEXT REFERENCES saved_payment_methods(payment_method_id),
    payment_status TEXT NOT NULL,
    estimated_delivery_time TEXT,
    estimated_pickup_time TEXT,
    order_received_at TEXT,
    preparing_started_at TEXT,
    ready_at TEXT,
    out_for_delivery_at TEXT,
    delivered_at TEXT,
    cancelled_at TEXT,
    cancellation_reason TEXT,
    created_at TEXT NOT NULL,
    updated_at TEXT NOT NULL
);

-- order_items table
CREATE TABLE order_items (
    order_item_id TEXT PRIMARY KEY,
    order_id TEXT NOT NULL REFERENCES orders(order_id),
    menu_item_id TEXT NOT NULL REFERENCES menu_items(menu_item_id),
    item_name TEXT NOT NULL,
    base_price NUMERIC NOT NULL,
    selected_size TEXT,
    size_price_adjustment NUMERIC NOT NULL DEFAULT 0,
    selected_addons JSONB NOT NULL DEFAULT '[]',
    selected_modifications JSONB NOT NULL DEFAULT '[]',
    special_instructions TEXT,
    quantity INTEGER NOT NULL,
    item_total_price NUMERIC NOT NULL,
    created_at TEXT NOT NULL
);

-- favorites table
CREATE TABLE favorites (
    favorite_id TEXT PRIMARY KEY,
    user_id TEXT NOT NULL REFERENCES users(user_id),
    restaurant_id TEXT NOT NULL REFERENCES restaurants(restaurant_id),
    created_at TEXT NOT NULL
);

-- reviews table
CREATE TABLE reviews (
    review_id TEXT PRIMARY KEY,
    user_id TEXT NOT NULL REFERENCES users(user_id),
    restaurant_id TEXT NOT NULL REFERENCES restaurants(restaurant_id),
    order_id TEXT REFERENCES orders(order_id),
    star_rating INTEGER NOT NULL,
    review_title TEXT,
    review_text TEXT NOT NULL,
    is_verified_visit BOOLEAN NOT NULL DEFAULT false,
    helpful_count INTEGER NOT NULL DEFAULT 0,
    is_edited BOOLEAN NOT NULL DEFAULT false,
    edited_at TEXT,
    created_at TEXT NOT NULL,
    updated_at TEXT NOT NULL
);

-- review_photos table
CREATE TABLE review_photos (
    photo_id TEXT PRIMARY KEY,
    review_id TEXT NOT NULL REFERENCES reviews(review_id),
    photo_url TEXT NOT NULL,
    display_order INTEGER NOT NULL DEFAULT 0,
    created_at TEXT NOT NULL
);

-- review_helpful_marks table
CREATE TABLE review_helpful_marks (
    helpful_mark_id TEXT PRIMARY KEY,
    review_id TEXT NOT NULL REFERENCES reviews(review_id),
    user_id TEXT NOT NULL REFERENCES users(user_id),
    created_at TEXT NOT NULL
);

-- review_reports table
CREATE TABLE review_reports (
    report_id TEXT PRIMARY KEY,
    review_id TEXT NOT NULL REFERENCES reviews(review_id),
    reported_by_user_id TEXT NOT NULL REFERENCES users(user_id),
    reason TEXT NOT NULL,
    additional_details TEXT,
    status TEXT NOT NULL DEFAULT 'pending',
    reviewed_at TEXT,
    created_at TEXT NOT NULL
);

-- discount_redemptions table
CREATE TABLE discount_redemptions (
    redemption_id TEXT PRIMARY KEY,
    discount_id TEXT NOT NULL REFERENCES discounts(discount_id),
    user_id TEXT NOT NULL REFERENCES users(user_id),
    order_id TEXT REFERENCES orders(order_id),
    redemption_method TEXT NOT NULL,
    discount_amount_applied NUMERIC NOT NULL,
    redeemed_at TEXT NOT NULL
);

-- badges table
CREATE TABLE badges (
    badge_id TEXT PRIMARY KEY,
    badge_name TEXT UNIQUE NOT NULL,
    badge_description TEXT NOT NULL,
    badge_icon_url TEXT,
    criteria_type TEXT NOT NULL,
    criteria_value INTEGER NOT NULL,
    created_at TEXT NOT NULL
);

-- user_badges table
CREATE TABLE user_badges (
    user_badge_id TEXT PRIMARY KEY,
    user_id TEXT NOT NULL REFERENCES users(user_id),
    badge_id TEXT NOT NULL REFERENCES badges(badge_id),
    is_showcased BOOLEAN NOT NULL DEFAULT false,
    showcase_order INTEGER,
    earned_at TEXT NOT NULL
);

-- verifications table
CREATE TABLE verifications (
    verification_id TEXT PRIMARY KEY,
    user_id TEXT NOT NULL REFERENCES users(user_id),
    restaurant_id TEXT NOT NULL REFERENCES restaurants(restaurant_id),
    verification_method TEXT NOT NULL,
    order_id TEXT REFERENCES orders(order_id),
    verified_at TEXT NOT NULL
);

-- saved_addresses table
CREATE TABLE saved_addresses (
    address_id TEXT PRIMARY KEY,
    user_id TEXT NOT NULL REFERENCES users(user_id),
    address_label TEXT NOT NULL,
    street_address TEXT NOT NULL,
    apartment_suite TEXT,
    city TEXT NOT NULL,
    state TEXT NOT NULL,
    zip_code TEXT NOT NULL,
    is_default BOOLEAN NOT NULL DEFAULT false,
    created_at TEXT NOT NULL,
    updated_at TEXT NOT NULL
);

-- notifications table
CREATE TABLE notifications (
    notification_id TEXT PRIMARY KEY,
    user_id TEXT NOT NULL REFERENCES users(user_id),
    notification_type TEXT NOT NULL,
    message TEXT NOT NULL,
    action_url TEXT,
    is_read BOOLEAN NOT NULL DEFAULT false,
    created_at TEXT NOT NULL,
    read_at TEXT
);

-- search_history table
CREATE TABLE search_history (
    search_id TEXT PRIMARY KEY,
    user_id TEXT NOT NULL REFERENCES users(user_id),
    search_query TEXT NOT NULL,
    search_type TEXT NOT NULL,
    created_at TEXT NOT NULL
);

-- user_statistics table
CREATE TABLE user_statistics (
    stat_id TEXT PRIMARY KEY,
    user_id TEXT UNIQUE NOT NULL REFERENCES users(user_id),
    total_reviews_written INTEGER NOT NULL DEFAULT 0,
    total_restaurants_visited INTEGER NOT NULL DEFAULT 0,
    total_favorites_saved INTEGER NOT NULL DEFAULT 0,
    total_orders_placed INTEGER NOT NULL DEFAULT 0,
    total_badges_earned INTEGER NOT NULL DEFAULT 0,
    total_discounts_redeemed INTEGER NOT NULL DEFAULT 0,
    unique_cuisines_tried JSONB NOT NULL DEFAULT '[]',
    updated_at TEXT NOT NULL
);

-- password_reset_tokens table
CREATE TABLE password_reset_tokens (
    token_id TEXT PRIMARY KEY,
    user_id TEXT NOT NULL REFERENCES users(user_id),
    reset_token TEXT UNIQUE NOT NULL,
    expires_at TEXT NOT NULL,
    is_used BOOLEAN NOT NULL DEFAULT false,
    created_at TEXT NOT NULL
);

-- dismissed_recommendations table
CREATE TABLE dismissed_recommendations (
    dismissal_id TEXT PRIMARY KEY,
    user_id TEXT NOT NULL REFERENCES users(user_id),
    restaurant_id TEXT NOT NULL REFERENCES restaurants(restaurant_id),
    dismissed_until TEXT NOT NULL,
    created_at TEXT NOT NULL
);

-- weekly_local_picks table
CREATE TABLE weekly_local_picks (
    pick_id TEXT PRIMARY KEY,
    restaurant_id TEXT NOT NULL REFERENCES restaurants(restaurant_id),
    week_start_date TEXT NOT NULL,
    week_end_date TEXT NOT NULL,
    featured_description TEXT,
    display_order INTEGER NOT NULL DEFAULT 0,
    selection_criteria TEXT,
    created_at TEXT NOT NULL
);

-- test_sync_table2 table
CREATE TABLE test_sync_table2 (
    id SERIAL PRIMARY KEY,
    test_data TEXT
);

